CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `vempleados` AS select `tiempos`.`tiempleado`.`idempleado` AS `Ficha Empleado`,`tiempos`.`tiempleado`.`nombre` AS `Nombre Empleado`,`tiempos`.`tiempleado`.`categoria` AS `Categoria`,`tiempos`.`ticcosto`.`idccosto` AS `CenCos`,`tiempos`.`ticcosto`.`descripcion` AS `Descripcion CenCos`,`tiempos`.`ticcosto`.`idpadre` AS `CenCos Padre`,`tiempos`.`tiempleado`.`fingreso` AS `Fecha de Ingreso`,`tiempos`.`tiempleado`.`email` AS `Correo Electronico` from (`ticcosto` join `tiempleado`) where `tiempos`.`tiempleado`.`ccosto` = `tiempos`.`ticcosto`.`idccosto`;
